);

CREATE TABLE message (
                         id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
                         user_id INT UNSIGNED NOT NULL,
                         is_bot BOOLEAN DEFAULT FALSE,
                         message TEXT NOT NULL,
                         date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
